Coding the model in Stan


Click here to see details about how to code the model in Stan

The full code for our Stan model is

// Code for ODE
functions {
  real[] sir(real t, real[] y, real[] theta, 
             real[] x_r, int[] x_i) {

      real S = y[1];
      real I = y[2];
      real R = y[3];
      real N = x_i[1];

      real beta = theta[1];
      real gamma = theta[2];

      real dS_dt = -beta * I * S / N;
      real dI_dt =  beta * I * S / N - gamma * I;
      real dR_dt =  gamma * I;

      return {dS_dt, dI_dt, dR_dt};
  }
}

data {
  int&lt;lower=1&gt; n_days;
  real y0[3];
  real t0;
  real ts[n_days];
  int N;
  int cases[n_days];
}

// Track status of individuals in SIR model in x
transformed data {
  real x_r[0];
  int x_i[1] = { N };
}

// Truncated prior using &lt;lower=...&gt;
parameters {
  real&lt;lower=0&gt; beta;
  real&lt;lower=0&gt; gamma;
  real&lt;lower=0&gt; phi_inv_sqrt;
}

transformed parameters{
  real y[n_days, 3];
  real phi = 1. / (square(phi_inv_sqrt));
  {
    real theta[2];
    theta[1] = beta;
    theta[2] = gamma;

    y = integrate_ode_rk45(sir, y0, t0, ts, theta, x_r, x_i);
  }
}

model {
  //priors 
  beta ~ normal(1.7, 1.4); 
  gamma ~ normal(0.2, 0.53);
  phi_inv_sqrt ~ normal(0, 1);

  // sampling distribution
  // col(matrix x, int n) - The n-th column of matrix x. 
  // Here the number of infected people 
  cases ~ neg_binomial_2(col(to_matrix(y), 2), phi);
}

// Quantities of interest
generated quantities {
  real R0 = beta / gamma;
  real recovery_time = 1 / gamma;
  real pred_cases[n_days];
  pred_cases = neg_binomial_2_rng(col(to_matrix(y), 2), phi);
}
